import React, { useEffect, useState } from 'react'
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

import '../App.css'
import Reviewme from './Reviewme';
import UpdateContactInfo from './UpdateContactInfo';
import groupLogo from '../assets/KPT-Logo---01.png'
import logoWhite from '../assets/Karakafamily-logo---01.png'


function Home() {

    const [backendData, setBackendData] = useState([{}]);
    const [emailData, setEmailData] = useState({
        to: '',
        name: '',
        message: '',
        googlelink: '',
        facebooklink: '',
    });

    const [googleLInk, setGoogleLink] = useState("");
    const [facebookLInk, setFacebookLink] = useState("");

    const fetchGoogleLink = () => {
        fetch("/Links/googleLink.txt")
            .then((response) => response.text())
            .then((text) => {
                setGoogleLink(text);
                setEmailData({ ...emailData, googlelink: text });
            })
            .catch((error) => console.error("Error reading the file: ", error));
    }

    const fetchFacebookLink = () => {
        fetch("/Links/facebookLink.txt")
            .then((response) => response.text())
            .then((text) => {
                setFacebookLink(text);
                setEmailData({ ...emailData, facebooklink: text });
            })
            .catch((error) => console.error("Error reading the file: ", error));
    }


    const handleChange = (e) => {
        setEmailData({ ...emailData, [e.target.name]: e.target.value });
    };


    useEffect(() => {
        fetch("/api").then(
            response => response.json()
        ).then((data) => {
            setBackendData(data);
        }).catch((err) => {

        });

        fetchGoogleLink();
        fetchFacebookLink();
    }, [])


    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            await axios.post('http://localhost:5000/send-email', emailData);
            alert('Email sent successfully');
        } catch (error) {
            console.error('Error sending email:', error);
            alert('Error sending email');
        }

    };



    const navigate = useNavigate();
    return (

        <div className='background-wrapper h-100'>
            <div className="d-flex flex-column text-center text-white  justify-content-between" style={{ height: "250px" }} >
                <div>
                    <img src={groupLogo} style={{ height: '35px' }} alt="GroupLogo" />
                </div>
                <div >
                    <img src={logoWhite} style={{ height: '45px' }} alt="GroupLogo" />
                </div>
                <div className="fw-light h3 text-sub" >
                    We'd Love to Hear Your Thoughts and Feedback!
                    {(typeof backendData.users === 'undefined') ? (
                        <p>Loading ...</p>
                    ) : (
                        backendData.users.map((user, i) => (
                            <p key={i}> {user} </p>
                        ))
                    )}

                    <form onSubmit={handleSubmit}>
                        <input
                            type="email"
                            name="to"
                            placeholder="Recipient Email"
                            value={emailData.to}
                            onChange={handleChange}
                            required
                        />
                        <input
                            type="text"
                            name="name"
                            placeholder="name"
                            value={emailData.subject}
                            onChange={handleChange}
                            required
                        />
                        <textarea
                            name="message"
                            placeholder="Message"
                            value={emailData.message}
                            onChange={handleChange}
                            required
                        />
                        <button type="submit">Send Email</button>
                    </form>

                </div>


                <div className='d-flex justify-content-center gap-3 mt-5'>
                    <button className='button' onClick={() => navigate('/reviewme')}>ReviewMe</button>
                    <button className='button' onClick={() => navigate('/updatecontact')}>Update Contact</button>
                </div>
            </div>


        </div >

    )
}

export default Home
