import React, { useState, useEffect } from 'react';
import NavLink from './Navbar';
import emailjs from '@emailjs/browser';

function Reviewme() {
    const [email, setEmail] = useState('cerin.abraham@gmail.com');
    const [firstName, setFistName] = useState('Cerin');
    const [lastName, setLastName] = useState('Abraham');
    const [googleLink, setGoogleLInk] = useState('Please find the links for Google');
    const [facebookLink, setFacebookLink] = useState('Please find the link for Facebook');

    const handleSubmit = (e) => {
        e.preventDefault();
        const serviceId = 'service_rkliftr';
        const templateId = 'template_j5123dj';
        const publicKey = 'uaevzCuvbpVxUlj4Y';

        const templateParams = {
            to_name: firstName + ' ' + lastName,
            to_email: email,
            from_name: "ReviewMe KPT",
            reply_to: "praise.cerinc@gmail.com",
            googleLink: googleLink,
            facebookLink: facebookLink,

        }

        emailjs.send(serviceId, templateId, templateParams, publicKey)
            .then((response) => {
                console.log('Email sent successfully!', response);
                setEmail('');
                setFistName('');
                setLastName('');
            })
            .catch((error) => {
                console.error('Error sending email', error);
            });

    }
    return (
        <div className='d-flex flex-column align-items-center bg-praimary'>
            <NavLink />
            <div className=' d-flex flex-column justify-content-start align-items-center border border-danger w-50' style={{ height: "90vh" }}>
                <div className=' d-flex flex-column justify-content-evenly align-items-center border border-success' style={{ height: "20%" }}>
                    <div className=''>
                        ReviewMe
                    </div>
                    <form onSubmit={handleSubmit} className='emailForm'>
                        <input
                            type='text'
                            placeholder='Your First Name'
                            value={firstName}
                            onClick={(e) => setFistName(e.target.value)}
                        />
                        <input
                            type='text'
                            placeholder='Your Last Name'
                            value={lastName}
                            onClick={(e) => setLastName(e.target.value)}
                        />
                        <input
                            type='email'
                            placeholder='Your email'
                            value={email}
                            onClick={(e) => setEmail(e.target.value)}
                        />
                        <button type='submit'>Send Email</button>

                    </form>
                </div>

            </div >

        </div >
    )
}

export default Reviewme
