import React from 'react'

function Feedback() {

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {

      const responseLink1 = await fetch('/Links/googleLink.txt');
      const link1Text = await responseLink1.text();
      setLink1(link1Text);

      const responseLink2 = await fetch('/Links/facebookLink.txt');
      const link2Text = await responseLink2.text();
      setLink2(link2Text);


      await axios.post('http://localhost:5000/send-email', {
        ...emailData,
        googlelink: link1Text,   // Pass link1 to the backend
        facebooklink: link2Text,   // Pass link2 to the backend
      });
      alert('Email sent successfully');
    } catch (error) {
      console.error('Error sending email:', error);
      alert('Error sending email');
    }

  };


  return (
    <div className=' blockSection bgBlock d-flex flex-column justify-content-between align-items-center '>
      <div className='text-white'>
        <h2 className='textMain'>We Value Your Feedback</h2>
        <h5 className='textSecondary'>Sorry to Hear That!</h5>
        <h5 className='textSecondary'>Help Us Understand How We Can Improve</h5>
      </div>
      <div className='mt-0'>

        <form onSubmit={handleSubmit}>
          <input
            type="email"
            name="to"
            placeholder="Recipient Email"
            value={emailData.to}
            onChange={handleChange}
            required
          />
          <input
            type="text"
            name="name"
            placeholder="name"
            value={emailData.name}
            onChange={handleChange}
            required
          />
          <textarea
            name="message"
            placeholder="Message"
            value={emailData.message}
            onChange={handleChange}
            required
          />
          <button type="submit">Send Email</button>
        </form>


      </div>

    </div>
  )
}

export default Feedback
