import React, { useEffect, useState } from 'react'
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Feedback() {
  const [emailData, setEmailData] = useState({
    to: '',
    name: '',
  });



  const handleChange = (e) => {
    setEmailData({ ...emailData, [e.target.name]: e.target.value });
    console.log("email data", emailData);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {

      await axios.post('http://localhost:5000/send-feedback', {
        ...emailData
      });
      alert('Email sent successfully');
    } catch (error) {
      console.error('Error sending email:', error);
      alert('Error sending email');
    }
    navigate('/')
  };


  const navigate = useNavigate();

  return (
    <div className=' blockSection bgBlock d-flex flex-column justify-content-between align-items-center '>
      <div className='text-white'>
        <h2 className='textMain'>We Value Your Feedback</h2>
        <h5 className='textSecondary'>Sorry to Hear That! Help Us Understand How We Can Improve</h5>
      </div>
      <div className='mt-0 d-flex flex-column justify-content-between align-items-center'>

        <form onSubmit={handleSubmit} className=' textSecondary w-100 mt-0 d-flex flex-column justify-content-between align-items-left gap-1' style={{ width: "70%" }}>
          <div>
            <label> Full Name</label>
            <input
              type="text"
              name="name"
              placeholder="Enter your name"
              value={emailData.name}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label> Email</label>
            <input
              type="email"
              name="to"
              placeholder="Email"
              value={emailData.to}
              onChange={handleChange}
              required
            />
          </div>

          <div>
            <label> Feedback message</label>
            <textarea
              name="message"
              placeholder="Feedback"
              value={emailData.message}
              onChange={handleChange}
              required
            />
          </div>

          <button type="submit">Send Email</button>
        </form>


      </div>

    </div>
  )
}

export default Feedback
